mapper.update:
UPDATE coin_bank
SET name = #{name}, memo = #{memo}, angry_limit = #{angryLimit}, reward = #{reward}
WHERE id = #{id} and member_id = #{memberId}

mapper.select:
SELECT member_id, expired
FROM coin_bank
WHERE id = #{id}

mapper.update:
UPDATE coin_bank
SET expired = 1
WHERE id = #{id}
