mapper.select:
SELECT COUNT(*)
FROM angry_phase
WHERE id = #{angryPhaseId}

SELECT COUNT(*)
FROM coin_bank
WHERE id = #{coinBankId} AND member_id = #{memberId}

SELECT COUNT(*)
FROM coin_bank
WHERE id = #{coinBankId} AND member_id = #{memberId} AND expired = #{expired};

SELECT COUNT(*)
FROM diary
WHERE id = #{diaryId};

SELECT COUNT(*)
FROM diary
WHERE id = #{diaryId} AND member_id = #{memberId}

SELECT COUNT(*)
FROM diary_file
WHERE diary_id = #{diaryId} AND file_id = #{fileId}

SELECT is_public
FROM diary
WHERE id = #{diaryId}

SELECT IFNULL(MAX(id), -1)
FROM diary
WHERE member_id = #{memberId} AND coin_bank_id = #{coinBankId};

SELECT IFNULL(MAX(id), -1)
FROM diary
WHERE member_id = #{memberId} AND YEAR(write_date) = #{writeYear} AND MONTH(write_date) = #{writeMonth}

SELECT IFNULL(MAX(id), -1)
FROM top_diary_daily
WHERE YEAR(write_date) = #{writeYear} AND MONTH(write_date) = #{writeMonth} AND DAY(write_date) = #{writeDay}

SELECT id AS diary_id, member_id, diary_no, title, content, DATE(write_date) AS write_date, angry_phase_id, is_public, todack_count, view_count
FROM diary
WHERE member_id = #{memberId} AND coin_bank_id = #{coinBankId} AND id < #{lastDiaryId}
ORDER BY id DESC
LIMIT #{size}

SELECT id AS diary_id, member_id, diary_no, title, content, DATE(write_date) AS write_date, angry_phase_id, is_public, todack_count, view_count
FROM diary
WHERE member_id = #{memberId} AND YEAR(write_date) = #{writeYear} AND MONTH(write_date) = #{writeMonth} AND id < #{lastDiaryId}
ORDER BY id DESC
LIMIT #{size}

SELECT COUNT(*)
FROM diary
WHERE coin_bank_id = #{coinBankId} AND member_id = #{memberId};

SELECT SUM(todack_count)
FROM diary
WHERE coin_bank_id = #{coinBankId} AND member_id = #{memberId};

SELECT id, member_id, diary_no, title, write_date, angry_phase_id, todack_count, view_count
FROM diary
WHERE is_public = 1 AND (title LIKE CONCAT('%', #{searchKeyword}, '%') OR content LIKE CONCAT('%', #{searchKeyword}, '%')) AND id < #{lastDiaryId}
ORDER BY id desc
LIMIT #{size}
