mapper.select:
SELECT id, email, nickname, member.password, register_type
FROM member
WHERE email = #{email}

mapper.update:
UPDATE member
SET nickname = #{nickname}
WHERE id = #{id}

UPDATE member
SET password = #{password}
WHERE id = #{id}

mapper.select:
SELECT id, email, nickname, register_type
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member
SET coin_bank_id = #{bankId}
WHERE id = #{id}

UPDATE member
SET coin_bank_id = NULL
WHERE id = #{id}

mapper.select:
SELECT IFNULL(coin_bank_id, 0)
FROM member
WHERE id = #{id}
