mapper.select:
SELECT profile.id, member.id as member_id, email, nickname, file.id as file_id, original_file_name, system_file_name
FROM profile, member, file
WHERE profile.member_id = #{id} and profile.member_id = member.id and file.id = profile.file_id;

mapper.update:
UPDATE profile
SET file_id = #{fileId}
WHERE id = #{id}
